function r(r){var t=1024*(1<<30),n=1<<30,o=1<<20,i=1024,e=Math.abs(r);return e>=t?Math.round(r/t*100)/100+"tb":e>=n?Math.round(r/n*100)/100+"gb":e>=o?Math.round(r/o*100)/100+"mb":e>=i?Math.round(r/i*100)/100+"kb":r+"b"}module.exports=function(e){var t,n,o,i;return"number"==typeof e?r(e):(t=e.match(/^(\d+(?:\.\d+)?) *(kb|mb|gb|tb)$/),n=parseFloat(t[1]),o=t[2],i={kb:1024,mb:1<<20,gb:1<<30,tb:1024*(1<<30)},i[o]*n)};